import React  from "react";
import data from './option';
class App extends React.Component{
  constructor(){
    super()
    this.countries = data;
    this.state ={suggestions:[]}
  }
  handlerOnchange(e){
       const filteredOptions = this.countries.filter((country)=>{
         if(country.slice(0,e.target.value.length) === e.target.value){
          return country
         }
       })
       this.setState({suggestions:filteredOptions})
  }

  render(){
    return(
      <>
      <input onChange={(e)=> this.handlerOnchange(e)}/>
      </>
    )
  }
}
export default App;
