import React  from "react";
import data from './option';
class App extends React.Component{
  constructor(){
    super()
    this.countries = data;
    this.state ={suggestions:[]}
  }
  handlerOnchange(e){
       const filteredOptions = this.countries.filter((country)=>{
         if(country.slice(0,e.target.value.length) === e.target.value){
          return country
         }
       })
       this.setState({suggestions:filteredOptions})
  }

  render(){
    let countriesToPrint;
    if(this.state.suggestions.length>0){
      countriesToPrint = this.state.suggestions.map((suggestion)=>{
        return <li key={suggestion}>{suggestion}</li>
      })
    }
    return(
      <>
      <input onChange={(e)=> this.handlerOnchange(e)}/>
      <ul>
        {countriesToPrint}
      </ul>
      </>
    )
  }
}
export default App;
